INSERT INTO contabilidade.plano_conta_estrutura (cod_uf, cod_plano, codigo_estrutural, titulo, funcao, natureza_saldo, escrituracao, natureza_informacao, indicador_superavit, atributo_tcepe) VALUES (16, COALESCE((SELECT MAX(cod_plano) FROM contabilidade.plano_conta_geral WHERE cod_uf = 16), 1),'3.7.1.1.1.10.00','IMPOSTO S/ OPERAÇÕES FINANCEIRAS - IOF','Registra as variações patrimoniais diminutivas com o imposto, de competência da união, sobre operações de crédito, câmbio e seguro, e sobre operações relativas a titulos e valores mobiliários que tem como fato gerador: I - quanto as operações de crédito, a sua efetivação pela entrega total ou parcial do montante ou do valor que constitua o objeto da obrigação, ou sua colocação a disposição do interessado; II - quanto as operações de câmbio, a sua efetivação pela entrega de moeda nacional ou estrangeira, ou de documento que a represente, ou sua colocação a disposição do interessado em montante equivalente a moeda estrangeira ou nacional entregue ou posta a disposição por este; III - quanto as operações de seguro, a sua efetivaçãopela emissão da apólice ou do documento equivalente, ou recebimento do prêmio, na forma da lei aplicável; IV - quanto as operações relativas a títulos e valores mobiliários, a emissão, transmissão, pagamento ou resgate destes, na forma da lei aplicável.','D','S','','',NULL);
