INSERT INTO contabilidade.plano_conta_estrutura (cod_uf, cod_plano, codigo_estrutural, titulo, funcao, natureza_saldo, escrituracao, natureza_informacao, indicador_superavit, atributo_tcepe) VALUES (16, COALESCE((SELECT MAX(cod_plano) FROM contabilidade.plano_conta_geral WHERE cod_uf = 16), 1),'4.3.3.1.1.39.00','SERVIÇOS EDUCACIONAIS','Registra as variações patrimoniais aumentativas provenientes das atividades do sistema educacional, cuja natureza esteja diretamente relacionada a formação do educando (matrículas, anuidades, etc...). as VPA de atividades auxiliares, de apoio ou derivadas dos serviços educacionais propriamente ditos, devem ser classificadas nos títulos apropriados. Exemplos: matrículas e anuidades, serviços educacionais, taxas de expedição de documentos, cópias xerográficas, heliográficas, etc.., serviço de credenciamento, autorização e reconhecimento de cursos.','C','S','','',NULL);
