CREATE OR REPLACE FUNCTION migra_cnae() RETURNS VOID AS $$
DECLARE
    stSQL       VARCHAR;
    reRecord    RECORD;
    inCodCnae   INTEGER;
    stNivel1    VARCHAR;
    stNivel2    VARCHAR;
    stNivel3    VARCHAR;
    stNivel4    VARCHAR;
    stNivel5    VARCHAR;
BEGIN
    -- corrige VIGENCIA 1
    DELETE FROM economico.nivel_cnae_valor;
    stSQL := '
                 SELECT cod_cnae
                      , cod_estrutural
                   FROM economico.cnae_fiscal
               ORDER BY cod_cnae
                      ;
             ';
    FOR reRecord IN EXECUTE stSQL LOOP

        stNivel1 := substr(reRecord.cod_estrutural, 1,1);
        stNivel2 := substr(reRecord.cod_estrutural, 3,2);
        stNivel3 := substr(reRecord.cod_estrutural, 6,1);
        stNivel4 := substr(reRecord.cod_estrutural, 7,3);
        stNivel5 := substr(reRecord.cod_estrutural,11,2);

        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 1
             , 1
             , reRecord.cod_cnae
             , stNivel1
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 2
             , 1
             , reRecord.cod_cnae
             , stNivel2
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 3
             , 1
             , reRecord.cod_cnae
             , stNivel3
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 4
             , 1
             , reRecord.cod_cnae
             , stNivel4
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 5
             , 1
             , reRecord.cod_cnae
             , stNivel5
             );

    END LOOP;

    -- inclui VIGENCIA 2
    SELECT MAX(cod_cnae)
      INTO inCodCnae
      FROM economico.cnae_fiscal
         ;

    stSQL := '
                 SELECT *
                   FROM migra_cnae
               ORDER BY cod_estrutural
                      ;
             ';
    FOR reRecord IN EXECUTE stSQL LOOP
        inCodCnae := inCodCnae + 1;

        INSERT
          INTO economico.cnae_fiscal
             ( cod_cnae
             , nom_atividade
             , cod_vigencia
             , cod_nivel
             , cod_estrutural
             )
        VALUES
             ( inCodCnae
             , reRecord.descricao
             , 2
             , reRecord.nivel
             , reRecord.cod_estrutural
             );

        stNivel1 := substr(reRecord.cod_estrutural, 1,1);
        stNivel2 := substr(reRecord.cod_estrutural, 3,2);
        stNivel3 := substr(reRecord.cod_estrutural, 6,1);
        stNivel4 := substr(reRecord.cod_estrutural, 7,3);
        stNivel5 := substr(reRecord.cod_estrutural,11,2);

        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 1
             , 2
             , inCodCnae
             , stNivel1
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 2
             , 2
             , inCodCnae
             , stNivel2
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 3
             , 2
             , inCodCnae
             , stNivel3
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 4
             , 2
             , inCodCnae
             , stNivel4
             );
        INSERT
          INTO economico.nivel_cnae_valor
             ( cod_nivel
             , cod_vigencia
             , cod_cnae
             , valor
             )
        VALUES
             ( 5
             , 2
             , inCodCnae
             , stNivel5
             );

    END LOOP;
END;
$$ LANGUAGE 'plpgsql';

SELECT        migra_cnae();
DROP FUNCTION migra_cnae();
DROP TABLE    migra_cnae;


